package com.app.secret.entity;import com.app.secret.core.base.entity.BaseEntity;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import javax.persistence.Column;import javax.persistence.Table;/** * @author kexiaohong */@Table(name = "APP_DEPT_ROLE")@ApiModel(value = "部门-角色")public class AppDeptRole extends BaseEntity {    @Column(name = "DEPT_ID")    @ApiModelProperty(value = "部门ID")    private java.lang.String deptId;    @Column(name = "ROLE_ID")    @ApiModelProperty(value = "角色ID")    private java.lang.String roleId;    public java.lang.String getDeptId() {        return this.deptId;    }    public void setDeptId(java.lang.String deptId) {        this.deptId = deptId;    }    public java.lang.String getRoleId() {        return this.roleId;    }    public void setRoleId(java.lang.String roleId) {        this.roleId = roleId;    }}