package com.app.secret.entity;import com.app.secret.core.base.entity.BaseEntity;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import javax.persistence.Column;import javax.persistence.Table;/** * @author kexiaohong */@Table(name = "APP_MOBILE")@ApiModel(value = "电话")public class AppMobile extends BaseEntity {    @Column(name = "NAME")    @ApiModelProperty(value = "名称")    private java.lang.String name;    @Column(name = "MOBILE")    @ApiModelProperty(value = "电话")    private java.lang.String mobile;    @Column(name = "TAG")    @ApiModelProperty(value = "标签")    private java.lang.String tag;    @Column(name = "DESC")    @ApiModelProperty(value = "备注")    private java.lang.String desc;    public java.lang.String getName() {        return this.name;    }    public void setName(java.lang.String name) {        this.name = name;    }    public java.lang.String getMobile() {        return this.mobile;    }    public void setMobile(java.lang.String mobile) {        this.mobile = mobile;    }    public java.lang.String getTag() {        return this.tag;    }    public void setTag(java.lang.String tag) {        this.tag = tag;    }    public java.lang.String getDesc() {        return this.desc;    }    public void setDesc(java.lang.String desc) {        this.desc = desc;    }}