package com.app.secret.entity;import com.app.secret.core.base.entity.BaseEntity;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import javax.persistence.Column;import javax.persistence.Table;/** * @author kexiaohong */@Table(name = "APP_OPT_LOG")@ApiModel(value = "操作日志")public class AppOptLog extends BaseEntity {    @Column(name = "OPT_NAME")    @ApiModelProperty(value = "操作人")    private java.lang.String optName;    @Column(name = "OPT_TIME")    @ApiModelProperty(value = "操作时间")    private java.util.Date optTime;    @Column(name = "OPT_INFO")    @ApiModelProperty(value = "操作信息")    private java.lang.String optInfo;    @Column(name = "OPT_URL")    @ApiModelProperty(value = "操作链接")    private java.lang.String optUrl;    public java.lang.String getOptName() {        return this.optName;    }    public void setOptName(java.lang.String optName) {        this.optName = optName;    }    public java.util.Date getOptTime() {        return this.optTime;    }    public void setOptTime(java.util.Date optTime) {        this.optTime = optTime;    }    public java.lang.String getOptInfo() {        return this.optInfo;    }    public void setOptInfo(java.lang.String optInfo) {        this.optInfo = optInfo;    }    public java.lang.String getOptUrl() {        return this.optUrl;    }    public void setOptUrl(java.lang.String optUrl) {        this.optUrl = optUrl;    }}