package com.app.secret.entity;import com.app.secret.core.base.entity.BaseEntity;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import javax.persistence.Column;import javax.persistence.Table;/** * @author kexiaohong */@Table(name = "APP_ROLE")@ApiModel(value = "操作日志")public class AppRole extends BaseEntity {    @Column(name = "ROLE_NAME")    @ApiModelProperty(value = "角色名称")    private java.lang.String roleName;    @Column(name = "ROLE_CODE")    @ApiModelProperty(value = "角色代码")    private java.lang.String roleCode;    @Column(name = "ROLE_DESC")    @ApiModelProperty(value = "角色描述")    private java.lang.String roleDesc;    public java.lang.String getRoleName() {        return this.roleName;    }    public void setRoleName(java.lang.String roleName) {        this.roleName = roleName;    }    public java.lang.String getRoleCode() {        return this.roleCode;    }    public void setRoleCode(java.lang.String roleCode) {        this.roleCode = roleCode;    }    public java.lang.String getRoleDesc() {        return this.roleDesc;    }    public void setRoleDesc(java.lang.String roleDesc) {        this.roleDesc = roleDesc;    }}