package com.app.secret.entity;import com.app.secret.core.base.entity.BaseEntity;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import javax.persistence.Column;import javax.persistence.Table;/** * @author kexiaohong */@Table(name = "APP_USER_ROLE")@ApiModel(value = "用户角色")public class AppUserRole extends BaseEntity {    @Column(name = "ROLE_ID")    @ApiModelProperty(value = "角色ID")    private java.lang.String roleId;    @Column(name = "USER_ID")    @ApiModelProperty(value = "用户ID")    private java.lang.String userId;    public java.lang.String getRoleId() {        return this.roleId;    }    public void setRoleId(java.lang.String roleId) {        this.roleId = roleId;    }    public java.lang.String getUserId() {        return this.userId;    }    public void setUserId(java.lang.String userId) {        this.userId = userId;    }}